INSERT INTO `opis_tipovye` (`id`, `name`, `text`) VALUES
(1, 'Описание типовых проектов', '<p><span style=\"color:#00AC55\"><span style=\"font-size:24px\">Что такое проект&nbsp;дома и из чего он состоит?</span></span> - пожалуй самый популярный вопрос посетителей нашего сайта. Итак, давайте разбираться. Типовой проект представляет из себя полный набор чертежей необходимых для строительства дома. В состав каждого готового проекта дома входит обязательный для приобретения комплект Архитектурно-строительных чертежей (АС), который из себя представляет два основных раздела: Архитектурный раздел (АР) и Конструктивный раздел (КР). Архитектурный раздел (АР) - описывает общие данные по проекту, архитектурные особенности строения, содержит планировочный решения по этажам, основные разрезы, фасады, маркировочные планы, ведомости оконных и дверных проемов, план крыши и т.д. Конструктивный раздел (КР) - содержит конструкторские чертежы фундамента, перекрытий и устройства полов, перемычек и стропильной системы. В проекте перечисляются все необходимые для строительства строительные материалы и их количество (объем и марка бетона, кол-во и сечение древесины, кол-во и марка арматуры, объемы кладки стен, площадь кровельного покрытия и т.д.) Более подробно о содержании разделов читайте в нашей статье &quot;Из чего состоит Архитектурно-строительный (АС) раздел проекта&quot;&nbsp;</p>\r\n');

